
public class Vikramsalary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
double salary=85000;
int tax=20;
System.out.println("total salary="+salary);
double taxreduction=(salary*tax)/100;
System.out.println("tax reduction="+taxreduction);
double totalsalary=salary-taxreduction;
System.out.println("salary after tax reduction="+totalsalary);

	}

}
