
public class swappping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=100;
int b=200;
int x=0;
System.out.println("Before swaping");
System.out.println(a+"\t"+b);
 x=a;
 a=b;
 b=x;
 System.out.println("After swaping");
 System.out.println(a+"\t"+b);

	}

}
