
public class swapwotemp3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=700;
int b=500;
int c=300;
System.out.println("before swapping :");
System.out.println(a+"\t"+b+"\t"+c);
a=b;
b=a-c+100;
c=a+200;
System.out.println("After swapping :");
System.out.println(a+"\t"+b+"\t"+c);

	}

}
