
public class swapwotemp4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=100;
int b=200;
int c=300;
int d=400;
System.out.println("before swapping :");
System.out.println(a+"\t"+b+"\t"+c+"\t"+d);
a=d-b;
b=a+100;
c=b+100;
d=b-a;
System.out.println("After swapping :");
System.out.println(a+"\t"+b+"\t"+c+"\t"+d);
	}
}
