
public class swapwtemp3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=100;
int b=200;
int c=300;
int temp=400;
System.out.println("before swapping :");
System.out.println(+a+"\t"+b+"\t"+c);
temp=a;
a=b;
b=c;
c=temp;
System.out.println("After swapping :");
System.out.println(+a+"\t"+b+"\t"+c);

	}

}
