
public class swapwtemp4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=100;
int b=200;
int c=300;
int d=400;
int temp=500;
System.out.println("before swapping :");
System.out.println(a+"\t"+b+"\t"+c+"\t"+d);
temp=a;
a=b;
b=c;
c=d;
d=temp;
System.out.println("After swapping :");
System.out.println(a+"\t"+b+"\t"+c+"\t"+d);
	}

}
